import React from 'react';
import  s from './FormControl.module.css';

export const RadioInput = ({input, meta,...props}) => {
    console.log(props, input, meta);

    return <div>
        <div><input {...props} {...input} {...meta}/> {props.text}</div>
{meta.error && <div className={s.radioInputSpan + ' ' + s.error}>{meta.error}</div>}
    </div>
}
