import React from 'react';
import s from './FormControl.module.css';

export const RadioInput = ({ input, meta, ...props }) => {
    console.log(props, input, meta);

    return <div>
        <div><input {...props} {...input} {...meta} /> {props.text}</div>
        <div className={s.radioInputSpan + ' ' + s.error}>{meta.error && meta.submitFailed && meta.error} </div>
    </div>
}
const FormControlRadio = ({ input, meta, children, ...props }) => {
    return (<div>
        {children}
        <span>meta.error</span>
    </div>)
}
export const RadioFieled = ({ input, meta, ...props }) => {
    return (
<FormControlRadio>
<div><input {...props} {...input} {...meta} /> {props.text}</div>
<div><input {...props} {...input} {...meta} /> {props.text}</div>
</FormControlRadio>
    )
}
