const SET_PRICE = 'SET_PRICE';

const initialState = {
    finalPrice:0,
}

const calculationReducer = (state = initialState, action) => {
    switch (action.type) {
        case SET_PRICE: 
        return { 
            ...state,
            finalPrice:action.price
        }

        default:
            return state;
    }

}

export const setPriceSucces = (price) => ({type: SET_PRICE, price})



export default calculationReducer
