import React from 'react';
import s from './cost.module.css'
import { Field} from 'redux-form';
import { requireFormRadio } from '../utils/validators/validators';


const QuestionItem = (props) => {
    console.log(props);

    return (
    <div className={s.answerItemBox}><label for={props.forLabel} className={s.answerLabel}>{props.responseText}
    </label><Field name={props.fieldName} component='input' type='radio' value={props.fieldValue} id={props.fieldId} validate={[requireFormRadio]}/></div>             
)
}

export default QuestionItem;
