import React from 'react';
import s from './FormControl.module.css';

export const RadioInputSpan = ({meta}) => {
   return <div className={s.radioInputSpan + ' ' + s.error}>
       {meta.error && meta.submitFailed ? <span>{meta.error}</span> : false}
       </div> 
}

