import React from 'react';
import s from './FormControl.module.css';

export const RadioInputSpan = ({meta}) => {
   return  <>{meta.error && meta.submitFailed ? <span className={s.radioInputSpan + ' ' + s.error}>{meta.error}</span> : false}</>

}
const Testim = ({input, meta, ...props}) => {
    return (
<div className={s.answerItemBox}><label for={props.forLabel} className={s.answerLabel}>{props.responseText}
</label><Field id={props.fieldId} /></div>  )
}

export const RadioInput = ({ input, meta, ...props }) => {
    console.log(props, input, meta);

    return <div>
        <div><input {...props} {...input} {...meta} /> {props.text}</div>
    </div>
}
